export default function handler(req, res) {
  // Bearerトークンの確認
  const token = req.headers.authorization?.split(' ')[1];
  if (!token || token !== process.env.EXPECTED_TOKEN) {
    return res.status(401).json({ error: 'Unauthorized' });
  }

  // ファイルの更新処理...
}
