import Head from 'next/head';
import { useState, useEffect } from 'react';

export default function HomePage() {
  const [serverUrl, setServerUrl] = useState('');

  useEffect(() => {
    setServerUrl(window.location.origin);
  }, []);

  const openapi = {
    openapi: '3.0.0',
    info: {
      title: 'File Operations API',
      description: 'API for reading, updating, and deleting files.',
      version: '1.0.0'
    },
    servers: [
      { url: 'https://next-agi.yuki36.repl.co/' }
    ],
    paths: {
      '/api/readFile': {
        get: {
          summary: 'Read a file',
          operationId: 'readFile',
          parameters: [
            { name: 'filename', in: 'query', required: true, schema: { type: 'string' } }
          ],
          responses: {
            '200': {
              description: 'File content retrieved successfully',
              content: {
                'application/json': {
                  schema: {
                    type: 'object',
                    properties: {
                      content: { type: 'string' }
                    }
                  }
                }
              }
            },
            '500': {
              description: 'Error reading file'
            }
          }
        }
      },
      '/api/updateFile': {
        put: {
          summary: 'Update a file',
          operationId: 'updateFile',
          requestBody: {
            required: true,
            content: {
              'application/json': {
                schema: {
                  type: 'object',
                  properties: {
                    filename: { type: 'string' },
                    content: { type: 'string' }
                  }
                }
              }
            }
          },
          responses: {
            '200': {
              description: 'File updated successfully'
            },
            '500': {
              description: 'Error updating file'
            }
          }
        }
      },
      '/api/deleteFile': {
        delete: {
          summary: 'Delete a file',
          operationId: 'deleteFile',
          parameters: [
            { name: 'filename', in: 'query', required: true, schema: { type: 'string' } }
          ],
          responses: {
            '200': {
              description: 'File deleted successfully'
            },
            '500': {
              description: 'Error deleting file'
            }
          }
        }
      },
      '/api/listFiles': {
        get: {
          summary: 'List files in a directory',
          operationId: 'listFiles',
          responses: {
            '200': {
              description: 'File list retrieved successfully',
              content: {
                'application/json': {
                  schema: {
                    type: 'object',
                    properties: {
                      files: {
                        type: 'array',
                        items: { type: 'string' }
                      }
                    }
                  }
                }
              }
            },
            '500': {
              description: 'Error listing files'
            }
          }
        }
      }
    }
  };

  return (
    <div className='container mx-auto px-4'>
      <Head>
        <title>File Operations API Documentation</title>
      </Head>

      <header className='bg-blue-500 text-white text-center p-4'>
        <h1 className='text-3xl font-bold underline'>File Operations API</h1>
      </header>

      <main className='my-8'>
        <pre className='bg-gray-100 p-4 rounded'>{JSON.stringify(openapi, null, 2)}</pre>
      </main>

      <footer className='bg-blue-500 text-white text-center p-4'>
        <p>Created by NEXT-AGI</p>
      </footer>
    </div>
  );
}
