import Head from 'next/head';
import { useState, useEffect } from 'react';

export default function HomePage() {
  const [serverUrl, setServerUrl] = useState('');

  useEffect(() => {
    // クライアント側で実行される場合、window.locationを使用してURLを取得
    setServerUrl(window.location.origin);
  }, []);

  return (
    <div className='container mx-auto px-4'>
      <Head>
        <title>File Operations API Documentation</title>
      </Head>

      <h1 className='text-3xl font-bold underline my-4'>File Operations API</h1>
      <p>This API provides operations for reading, updating, and deleting files.</p>

      <h2 className='text-2xl font-semibold my-3'>OpenAPI Specification</h2>
      <pre className='bg-gray-100 p-4 rounded'>{`
openapi: 3.0.0
info:
  title: File Operations API
  description: API for reading, updating, and deleting files.
  version: 1.0.0
servers:
  - url: ${serverUrl}
// 他の仕様はここに続く
      `}</pre>
    </div>
  );
}
