import Head from 'next/head';
import { useState, useEffect } from 'react';
import yaml from 'js-yaml';
import fs from 'fs';
import path from 'path';

export async function getStaticProps() {
  // YAMLファイルの読み込み
  const filePath = path.join(process.cwd(), 'openapi.yml');
  const fileContents = fs.readFileSync(filePath, 'utf8');
  const openapi = yaml.load(fileContents);

  return { props: { openapi } };
}

export default function HomePage({ openapi }) {
  const [serverUrl, setServerUrl] = useState('');

  useEffect(() => {
    setServerUrl(window.location.origin);
  }, []);

  return (
    <div className='container mx-auto px-4'>
      <Head>
        <title>File Operations API Documentation</title>
      </Head>

      <h1 className='text-3xl font-bold underline my-4'>File Operations API</h1>
      <pre className='bg-gray-100 p-4 rounded'>{JSON.stringify(openapi, null, 2)}</pre>
    </div>
  );
}
